import React from "react";
import { useState } from "react";

export default function Post({
  title,
  content,
  author,
  dateCreated,
  dispatch,
  completed,
  dateCompleted,
}) {
  console.log("Date created" + dateCreated);

  const current = new Date();
  const [dtCompleted, setDateCompleted] = useState(dateCompleted);
  const [complete, setCompleted] = useState(completed);

  const handleChange = (e) => {
    console.log(e.target.checked);
    var currentDate = `${current.getDate()}/${current.getMonth() +
      1}/${current.getFullYear()}`;
    if (e.target.checked) {
      setDateCompleted(currentDate);
      setCompleted("True");
    } else {
      setDateCompleted("");
      setCompleted("False");
    }
    console.log(dateCompleted);

    e.preventDefault();
    dispatch({
      type: "TOGGLE_TODO",
      title,
      content,
      author: author,
      dateCreated,
      dtCompleted,
      complete,
    });
  };

  const deletePost = (e) => {
    console.log("Delete post");
    e.preventDefault();
    dispatch({
      type: "DELETE_TODO",
      title,
      content,
      author: author,
      dateCreated,
    });
  };

  return (
    <div className="container" style={{ marginBottom: "40px" }}>
      <div className="row">
        <div className="col">
          <input
            type="checkbox"
            checked={completed === "True"}
            onClick={(event) => handleChange(event)}
          />
        </div>
        <div className="col">
          <h3>{title}</h3>
        </div>
      </div>
      <div className="row">
        <i>
          Description <b>{content}</b>
        </i>
      </div>
      <i>
        Written by <b>{author}</b>
      </i>
      <br></br>
      <i>
        Date Created <b>{dateCreated}</b>
      </i>
      <br></br>
      <i>
        Completed <b>{completed}</b>
      </i>
      <br></br>
      <i>
        Date Completion<b>{dateCompleted}</b>
      </i>
      <div>
        <input
          type="submit"
          value="Delete"
          onClick={(event) => deletePost(event)}
        />
      </div>
    </div>
  );
}
