function userReducer(state, action) {
  switch (action.type) {
    case "LOGIN":
    case "REGISTER":
      return action.username;
    case "LOGOUT":
      return "";
    default:
      return state;
  }
}

function postReducer(state, action) {
  switch (action.type) {
    case "CREATE_POST":
      const newPost = {
        title: action.title,
        content: action.content,
        author: action.author,
        dateCreated: action.dateCreated,
        dateCompleted: action.dateCompleted,
        completed: action.completed,
      };
      return [newPost, ...state];
    case "TOGGLE_TODO":
      const newState = Object.assign({}, state);
      newState.dateCompleted = action.dateCompleted;
      newState.completed = action.completed;
      return [newState, ...state];
    case "DELETE_TODO":
      const [post, setPosts] = useState(state);
      setPosts((current) =>
        current.filter((post) => post.title !== action.title)
      );
      console.log("Delete reducer");
      var idx = null;
      state.map((x, index) => {
        if (x.title === action.title) {
          idx = index;
        }
      });

      setData(state.filter((_, i) => idx !== i));

      state.delete(idx);
      return [state];
    default:
      return state;
  }
}
export default function appReducer(state, action) {
  return {
    user: userReducer(state.user, action),
    posts: postReducer(state.posts, action),
  };
}
